INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000714','신성계곡 녹색길','03코스','지소리돌보 ~ 하천 과수원길 ~ 백석탄 ~ 종점','경북 청송군','보통','5~10Km미만','4.7','지소리 돌보를 건너면 곧바로 계곡 옆으로 난 과수원이 길손을 반긴다. 과수원을 가로지르는 길,짧지만 추억에 물드는 길이다. 이어 절경의 암벽으로 둘러싸인 계곡의 수려함을 볼 수 있는 백석탄을 만난다. 하얀 바위에 크고 작은 조약돌이 무리지어 올라있어, 물이 바위에 넘쳐흐르면 반짝빛을 발하고, 마른 날이면 새알같이 매끈하다. 하얀 바위는 만지면 손에 흰 가루가 묻어날 것만 같다. 한동안 넋을 놓으면 시간은 덧없이 흐르고, 물줄기에 마음까지 흘려보내게 된다.','1시간 45분','고와휴게소, 종점','고와휴게소, 종점','고와휴게소, 종점','경북 청송군 안덕면 신성리 53-4',36.3216324,128.9888927);
