INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000716','황룡강 누리길','03코스 황룡강 마을 안길','임곡마을~성내마을~천동마을~산막습지~황룡강 벽림~노동마을~박산마을','광주 광산구','보통','5~10Km미만','8.9','80년대 시간이 멈처 있는 듯한 임곡의 거리와 황룡강을 따라가는 마을안길에서 고향의 정취를 느낄수 있으며 오월을 상징하는 이팝나무 사이로 광주의 들불과 의병의 혼을 만날 수 있는길이다.','3시간 30분','식수보급처가 없으니 사전준비','임곡동 주민센터','임곡동 농협 하나로 마트','광주 광산구 등임동',35.1942701,126.769501);
