INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000718','몰래길','01코스 성곡지 주변로','그린투어센타~(3.0km)제수지제방~(1.0km)쉼터~(2.5km)그린투어센타','경북 청도군','보통','5~10Km미만','6.5','○ 청도군 풍각면 성곡리에 성곡댐을 주변으로 데크가 설치되어 있어 휴식공간으로 활용이 되고 있으며, 코미디철가방극장에는 개그관람을 위한 방문객이 많이 찾고 있다. 인근하여 그린투어센터에서는 성곡댐을 조망권으로 성곡리 마을의 농촌체험을 즐길 수 있는 공간이 있다. ○ 풍각면 성곡리와 각북면 남산리를 잇는 몰래길은 주변이 소나무 숲길로 걸어가면 생태체험을 느낄 수 있다. ○ 몰래길을 넘어서면 각북면 남산리가 있어 비슬산자락의 오크랜드의 숙박시설, 군불로 찜질방, 식당 그리고 최복호패션문화연구소가 있어 구경거리가 많다. ○ 비슬산자락에 위치한 고찰 용천사, 예술촌도 자리를 잡아가고 있으며, 주변 식당, 찻집이 있다.','1시간 20분','그린투어센타 앞 음수대','인근 그린투어센타 및 코메디철가방 화장실 이용','그린투어센타내에 위치','경북 청도군 풍각면 성곡리 산 276-3',35.6523375,128.5882243);
