INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000720','범어사 문화체험 누리길','범어사 문화체험 누리길','청룡노포동 친수공원 ~ (2.3km)범어사(주차장)','부산 금정구','쉬움','1~5Km미만','2.3','금정구 청룡동 온천천 ~ 친수공원 ~ 범어사로 연결되는 누리길을 조성해 놓았다. 이 빼어난 경치 덕에 예로부터 사람의 발길이 잦았고, 계곡을 따라 정자가 많이 들어서 기존 자연경관을 가장 잘 보존한 길이며, 걷는 이가 마치 자연과 하나가 된 듯한 듯 황홀경에 빠지는 길이다.','40분','범어사 주차장에 설치되어 있음','범어사 주차장에 설치되어 있음','범어사 주차장에 설치되어 있음','부산광역시 영도구 청학동 330-76',35.1087007,129.0625176);
