INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000725','반송동 석대천변 누리길','해운대구 반송동','새반송교~(0.5km)반석교~(1.1km)반석2호교','부산 해운대구','쉬움','1~5Km미만','1.6','반송동 석대천변 누리길은 새반송교~반석2호교간 석대천 제방을 따라 조성된 산책길이다. 석대천 제방 아래에도 지역주민들이 건전한 여가를 즐길 수 있도록 산책로 및 자전거길이 조성되어 있으며, 수영강 시민공원까지 연결되어 하천을 따라 운동하기에 더없이 좋은 코스다. 석대천 주변은 개발제한구역으로 아직 개발이 되지 않아 타 하천과 달리 높은 빌딩, 아파트 사이를 걷는 것이 아니라 장산과 추마산을 바라보며 고즈넉하게 산책을 즐길 수 있으며 간혹 지나가는 경전철이 도심에 있음을 일깨워준다. 석대천 인근에는 반여농산물도매시장, 석대화훼단지가 위치하고 있어 주말에 가족들과 함께 걸어서 우리 농산물 구매, 각종 꽃 구경을 할 수 있으며, 지하철(반여농산물시장역, 석대역)을 이용하면 더욱 편리하게 누리길을 이용할 수 있다.','30분','식수보급처가 없으므로 사전준비','지하철 역사 및 반석2호교 인근 간이화장실','매점이 없으므로 사전준비','부산 해운대구 석대동',35.2183018,129.1356501);
