INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000740','입화산 참살이 숲 누리길','01코스','다운동 현대아파트 일원, 다운고등학교 일원~(1.2km)다운목장~(1.3km)입화산 정상~(2.5km)입화산 참살이 숲 야영장 ~ (1.3km)다운교 입구','울산 중구','보통','5~10Km미만','6.3','본 코스는 기존 등산로로 입구는 다운동 현대아파트, 다운고등학교 일원에 위치하고 있음. 특히 다운고등학교 인근에 위치한 척과천 일원으로 척과공원이 조성 추진중에 있으며, 본 코스에는 산 중턱에 초지와 갈대로 이루어진 다운목장과 울산시가지를 한 눈에 내려다 볼 수 있는 입화산 정상과 가족단위의 도심 속 휴식공간으로 조성된 각종편의시설(네이처 스포츠, 물놀이체험공간, 야영장) 및 울창한 숲 자원을 이용한 나무학습장, 습지관찰원, 편백나무 숲, 시노래비, 야외공연장 등 우리 구의 명소로 자리매김을 하고 있는 참살이 숲이 조성되어 있음. 아울러 더 나은 누리길 조성을 위하여 볼거리 제공 및 정비를 지속적으로 추진하고 있음','1시간 30분','입화산 참살이 숲 네이쳐 스포츠','입화산 참살이 숲 야영장 앞','출발점 다운동 현대아파트 및 다운동아파트 일원','울산 중구 다운동',35.5735619,129.2783013);
