INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000750','생명누리길','01코스 생명누리길','만수사~(2.1㎞)경흥사~(1.4㎞)이목지~(0.7㎞)차곡지','경북 경산시','보통','1~5Km미만','4.2','국토교통부는 개발제한구역 주민을 위해 누리길, 여가녹지조성, 경관, 전통문화 사업 등 환경·문화사업을 펼치고 있으며 누리길사업은 누구나 걷기 쉬운 자연지형을 활용해 지역의 역사·문화·자연자원과 연계한 탐방·체험 프로그램 개발로 만수사, 경흥사 등 고찰과 이목지, 차곡지 등의 빼어난 수변경관이 어우러지며, 산전동 분청사기요지 등의 역사문화자원을 몸으로 느끼며 자연과 동화되는 생명누리길이다. 단순히 걸으면서도, 스스로의 정화된 시간을 가지며, 삼성현의 얼이 서린 경산시의 문화와 자연을 느끼며, 더불어 국토사랑의 마음을 키울 수 있는 길이다. 어느덧 길을 걷다보면 분청사기의 재료인 지하맥반석의 기운을 온몸으로 받으며 심신정화의 시간을 가질 수 있는 생명누리길이다. 짧은 코스지만 문화유산도 많이 담겨있어 유익한 코스이다.','1시간 40분','만수사, 경흥사, 산전리마을회관','만수사, 경흥사, 산전리마을회관','없음','경북 경산시 남천면 산전리 1009-3',35.776792,128.7119315);
