INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000768','관동별곡 8백리길','01코스','통일전망대~(3.8km)명파해수욕장~(3.7km)금강산 콘도~(1.7km)대진항~(1.6km)초도해수욕장~(4km)화진포광장','강원 고성군','쉬움','10~15Km미만','14.8','우리나라 최북단, 가슴 아픈 분단의 현실을 여전히 실감할 수 있는 곳이다. 아이러니하게도 아픈 분단의 현실은 맑고 깨끗한 자연을 지켜주었으며, 도심에서는 느낄 수 없는 상쾌함을 전해주는 구간이다. 또한 강산과 설악산, 바다와 호수, 계곡이 어우러진 곳으로 김일성 별장, 이승만 별장 등 한국 현대사의 흔적이 산재해 있다. 노선의 시점인 통일전망대에 들어가기 위해서는 먼저 통일안보공원에서 민통선 출입을 위한 출입신고서를 작성하고 간단한 교육(영상물 시청)을 거쳐야 한다. 이때 반드시 일행 중 한 사람은 신분증을 지참해야 한다. 이 노선은 전체 구간을 걷기에는 불가능한 곳으로 주로 차량을 이용하고 민간인 출입이 허가된 해안가만을 걷고 DMZ 박물관 등을 방문하는 것으로 일정을 잡는게 좋다. 아쉽지만 전체 노선을 걷는 기쁨은 통일 이후로 미루어 두기로 하자.','4시간','','초도해수욕장, 초도항 인근 2개소','','강원 고성군 죽왕면 문암진리 466-2',38.3117901,128.539146);
