INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000771','북한산둘레길','01코스 소나무숲길','우이우이령길입구~(0.6km)둘레길안내소(우이분소)~(1.1km)만고강산약수터~(0.9km)솔밭근린공원입구~(0.5km)솔밭근린공원상단','서울 강북구','쉬움','1~5Km미만','3.1','북한산 둘레길 01코스 소나무 숲길. 높은 산은 자신없는 분, 하지만 맑은 산공기를 마시며 산책을 즐기고 싶은 분들에게 추천해드립니다. 이 길은 전체적으로 완만한 산길로 이루어져 있어 둘레길을 처음 걸으시는 분이라면 가장 먼저 와볼 만한 구간입니다. 우이계곡 물소리를 들으며 시작한 길이 맑은 약수로 가득한 만고강산을 지나 천여 그루의 소나무가 빼곡히 자라고 있는 솔밭근린공원에 이를 때 쯤이면, 입욕을 한 듯 온몸에 피톤치드 향이 가득할 것입니다. 이미 소나무 숲길을 걸으신 분이라면 우리 곁에 있었지만 그냥 지나치기만 했던 문화, 역사 자료를 찾아 소나무숲길 곳곳으로 문화 기행을 떠나 보는 것은 어떨까요?','1시간 30분','만고강산 약수터','제일교회 모바일화장실 위치','중간중간 마을길 통과구간 매점 활용','서울 은평구 불광동',37.6261212,126.9362684);
