INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000772','해오름길','01코스','낙산사~(5.5km)낙산대교~(3.5km)오산 해수욕장~(3.7km)동호 해수욕장~(2.5km)여운포교~(4.8km)하조대','강원 양양군','보통','5~10Km미만','20','전진항과 오산·동호해변을 지나는 해오름길은 동해 바다의 비경을 고스란히 경험할 수 있는 길이다. 천년 고찰과 그림 같은 풍경의 해변, 옛 시인 묵객의 낭만이 서린 정자를 품고 있다. 여정은 먼저 2005년 화재 이후 복원된 낙산사를 돌아본 후 시작한다. 바닷가의 야트막한 산등성에 자리한 낙산사는 명실상부한 양양 최고의 명소다. 신라 문무왕 때인 671년에 당대의 고승인 의상대사가 창건한 고찰로 천년이 넘는 세월이 지나는 동안 수차례에 걸쳐 중건을 거듭했다. 강원도 양양 땅은 이른바 ‘해돋는 마을’이라 불리는 곳. 옛 선인들은 양양 땅 가운데서도“해돋는 모습은 낙산사 앞바다가 으뜸이다”라고 칭송했다.또한 해오름길의 종점인 하조대는 기암괴석과 쪽빛바다가 어우러진 환상의 세계처럼 신비감이 들 만큼 아름다운 경관을 자랑한다.','7시간','','전진항, 낙산해수욕장, 낙산도립공원, 수산항, 동호해수욕장, 하조대해수욕장, 하조대 인근 13개소','','경북 경산시 압량읍 신월리 2-3',35.8270846,128.7897151);
