INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000773','미숭산 둘레길','01코스(월막리 ~ 고곡리)','쌍림면 월막리~(1.1㎞)쌍림면 고곡리','경북 고령군','쉬움','1~5Km미만','1.1','쌍림면 월막리에서 쌍림면 고곡리로 이어지는 등산로이다. 골짜기와 산자락을 연결하는 비교적 평이한 노선이며 전.답과 연접하여 농촌풍경을 느낄수 있는 등산로이다. 쌍림면 고곡리로 내려서면 조선시대 의병장인 김면 장군을 모시는 사당과 유적지가 있어 어린이 교육에도 적합하다. 가족단위 관광객이나 가벼운 트레킹을 즐기고 싶은 이들에게 안성맞춤이다. 문화유산과 함께 어우러진 1코스는 훌륭한 학습체험장과 숲길을 보유하고 있다.','30분','월막리 마을회관, 중간에는 없음','월막리 마을회관','월막리 마을회관, 중간에는 없음','경북 고령군 쌍림면 고곡리 513',35.7092103,128.2399073);
