INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000779','봉좌산 숲길','봉좌산 숲길','봉강재~ (4.0km) 봉좌산 정상~(2.3km) 분옥정 ~(1.2km) 도농교류센터~ (3.8km) 새마을운동 발상지 기념관~ (3.3km) 성터사거리 ~(2.1km) 봉좌산 정상 선돌메 ~(2.9km) 성터사거리 ~ (2.6km) 새마을운동발상지 기념관','경북 포항시 북구','어려움','20~100Km미만','24.4','봉좌산 숲길은 포항시 북구 기계면과 경시주 안강읍, 영천시 임고면 3개시에 걸쳐있는 봉좌산을 중심으로 포항시 북구 기계면 봉계리, 고지리, 문성리에 걸쳐 개설된 숲길로 포항시에서 추진 중인 "문성새마을권역 농촌마을종합개발사업"과 연계하고 특색 있는 지역의 문화를 반영하기 위해 기본구상 단계부터 인근 마을주민과 포항시가 참여하는 "봉좌산 민, 관협의회"를 구성하여 노선 및 주요시설을 확정하였다. 봉좌산, 봉강재, 선돌메, 분옥정, 새마을운동발상지기념관 등 지역사회의 역사문화유산과 보리수나무, 참꽃, 자작나무군락지 등 자연유산을 연계하여 다양한 체험을 할 수 있도록 봉좌산 숲길을 조성하였다.','3시간 (코스별)','봉좌마을 도농교류센터, 참샘이','봉좌마을 도농교류센터','','경북 포항시 북구 기계면 봉계리 산 43-8',36.0554599,129.1646227);
