INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000780','북한산둘레길','04코스 솔샘길','북한산생태숲앞~(0.5km)성북생태체험관~(0.6km)칼바위탐방로 갈림길~(1.0km)북한산탐방안내소','서울 강북구','보통','1~5Km미만','2.1','북한산 둘레길 04코스 솔샘길. 소나무와 맑은 샘이 있다는 뜻을 가진 솔샘길은 예쁜 이름처럼 아이들과 잘 어울리는 길입니다. 수십가지 야생화와 관목으로 가득한 생태공원에서 처음 보는 꽃과 나무의 이름을 부르고, 성북구에서 제공하는 무료 생태교육을 받다보면 모르는 사이에 생태 감수성이 무럭무럭 자라게 될 것입니다. 이 솔샘길에 있다는 솔샘 발원지는 어디에 있을까요? 솔샘길이 지나는 미향마을의 유래와 거북바위의 전설은 또 무엇일까요? 궁금증을 풀고 나면 옛 명칭이 ''청수장''인 북한산탐방안내소에 들러 재미있는 북한산 역사와 숲 이야기를 들어보세요.','1시간','식수보급처가 없으므로 중간중간 마을길 통과시 매점에서 구입하거나 사전준비','성북생태체험관 고정식 화장실 위치','성북생태체험관 과 정릉탐방안내소 사이에 편의점 위치','서울 은평구 불광동',37.6261212,126.9362684);
