INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000785','명품금강소나무 숲길','치악산 (무장애 탐방구간)','신흥동 주차장~(0.15km)구룡탐방지원센터~(0.35km)전망대~(0.9km)구룡사~(1.1km)구룡소','강원 원주시','매우쉬움','1~5Km미만','1.1','구룡문화재매표소를 지나서 시작되는 무장애탐방로는 인도와 차도가 분리되어 안전하게 이동할 수 있으며, 장애인·노약자·임산부·어린이등이 쉽게 이용할 수 있도록 데크폭을 넓히고 경사도를 낮추어 휠체어를 동반한 탐방도 가능하다. 계곡을 따라 놓여진 데크길을 따라가면 숲과 계곡이 어울어져 마치 계곡트레킹을 하는 느낌을 받는다. 또한 전망대에서 바라보는 경관은 탐방객들이 사진촬영을 많이 하는 곳이다. 특히 일주문부터 구룡사 구간은 부드러운 마사토가 깔려 있고 100년 이상된 금강소나무 군락지가 어울어져 최고의 경관을 자랑한다. 금강소나무 길을 지나면 수많은 고승의 발자취가 남아있는 구룡사와 만나게 된다. 구룡사를 지나면 마지막으로 용이 승천하였다는 전설이 깃든 용소를 만나게 되는데, 이 푸른빛의 구룡소를 보면 하루를 편안한 마음으로 마무리 할 수 있다.','왕복 1시간. 편도 30분','식수보급처는 없음.','구룡사 화장실(장애인화장실)','신흥동 주차장 옆 매점 4개소','강원 원주시 소초면 학곡리 1068',37.403075,128.0489161);
