INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000791','황산강 베랑길','01코스 베랑길','물금취수장 ~ (0.2km)물문화전시관 ~ (0.8km)수변부 데크종점 ~(0.1km) 자전거길인증센터 ~ (0.9km)원동취수장','경남 양산시','쉬움','1~5Km미만','2','역사속으로 사라진 과거 영남대로 3대 잔도의 하나인 황산잔도노선 복원으로 역사성을 회복하고, 수변으로 데크교량을 설치하여 주변 경관과 잘 어울리는 그림과 같은 풍광을 연출한다. 황산은 물금읍의 옛지명이며, 베랑길은 벼룻길의 양산방언이다. 주변 볼거리로는 임경대, 용화사, 가야진사 등이 있어 가족이나 주변 지인들과 가벼운 마음으로 산책하며 문화유적지를 방문하는 여유를 가질수 있다.','30분','식수보급처가 없으니 매점에서 구입하거나 사전 준비','물문화전시관 화장실','매점 및 편의점이 없으므로 사전 준비','경남 양산시 원동면 화제리 3076',35.3230924,128.9741196);
