INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000795','왕피천 은어길','왕피천 은어길','구산리 성산지 부근~(3km)굴구지 마을','경북 울진군','보통','1~5Km미만','3','왕피천 은어길은 구산리 성산지에서 굴구지 마을까지 연결된 3km의 탐방 구간으로, 왕피천 계곡로로 이루어져 있다. 이 구간은 왕피리로 이어지는 왕피천 생태탐방(14km)로와 연결되는데, 이는 생태경관보전지역으로 지정하여 관리되고 있는 지역으로 뛰어난 자연자원 및 경관자원을 체험할 수 있다. 한때 이곳 농업 기반인 관개수로를 따라 걷게 되면서 안정적으로 이동하면서 여유롭게 풍광을 즐길 수 있다.','1시간','','성산지 부군','','경북 울진군 근남면 구산리 산 175',36.929187,129.3558383);
