INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000797','일월산숲길','01코스 아름다운 숲길','일월산 자생화공원. 삼층석탑 ~ 윗대티 입구 ~ 아름다운 숲길 입구 ~ 진등 갈림길 ~ 칠밭목 갈림길 ~ 뿌리샘 갈림길 ~ 큰골 갈림길 ~ 윗대티 마을 ~ 윗대티 주차장 ~ 아름다운 숲길 입구 ~ 윗대티 입구 ~ 일월산 자생화공원','경북 영양군','쉬움','5~10Km미만','7','아름다운 숲길은 영양군 일월면과 봉화군 재산면을 잇는 31번 국도였다. 일제강점기 때 구리광산이 생기자 광물과 금강송을 실어가기 위해 산의 등줄기를 잘라내고 길을 내었다. 수탈의 길이자 훼손의 아픈 역사를 간직한 이 길은 잘 포장된 우회도로가 생기면서 잊혀졌다. 그러나 30여 가구 50여 명의 대티골 사람들이 아름다운 숲길을 재생해 내었다. 이 길은 2009년 산림첨에서 선정하는 ''아름다운 숲 전국대회''에서 어울림상을 수상하기도 한 아름다운 숲길이다.','4시간10분','식수보급처가 없으니 매점에서 구입하거나 사전준비','일월산 자생화공원, 윗대티 입구, 윗대티 주차장','일월산자생화공원','경북 영양군 일월면 용화리 산 77-13',36.8220184,129.0986955);
--행 798
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000798','북한산둘레길','12코스 충의길','사기막골 입구 ~ (0.4km)사기막 전망대 ~ (2.8km)솔고개 ~ (0.5km)교현우이령입구','서울 강북구','보통','1~5Km미만','3.7','북한산 둘레길 12코스 충의길. 사기막계곡에서 솔고개로 이어지는 숲 속 길입니다. 특히 상장봉 왼쪽 사면을 가로지르는 숲 속 길은 새롭게 조성된 산책로로 인적이 드물고 산세가 아름답습니다. 충의길 시작점인 사기막계곡을 출발해 새로 놓인 다리를 건나 십분여 산길을 오르면 사기막전망대에 도착합니다. 목재 데크 안에 의자를 놓아 쉼터를 겸하도록 설치된 이 전망대에서는 북한산의 주봉인 인수봉과 백운대가 한 눈에 들어옵니다. 또한 이곳은 둘레길은 물론 북한산 숨은벽 능선을 조망할 수 있는 전망대로 경치가 매우 좋은 코스입니다.','1시간 45분','식수보급처가 없으므로 중간중간 마을길 통과시 매점에서 구입하거나 사전준비','사기막공원지킴터에 모바일화장실 위치','사기막골 입구 길건너편에 매점 위치','서울 은평구 불광동',37.6261212,126.9362684);
--행 799
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000799','문화유산여행길','01코스 수승대 문화유산 여행길(수승대 트레킹길)','수승대~척수대~정온종택~모리재~강선대~고인돌~만월당~갈계숲~행기숲~해인정~농산석조여래상~용암정~수승대(14km)','경남 거창군','보통','10~15Km미만','14','조선조 기개와 정절의선비로 알려진 정온 동계선생의 생가와 말년에 은거하던 모리재를 이어주는 숲길로 동계선생이 이길로 다니시던걸로 추정한다. 숲길은 걷기 편안하고 한여름에도 울창한 소나무숲이 만들어내는 그늘로 시원하고 상그럽다. 도보 30분 반경 거리내에 수승대, 갈계숲, 용암정, 강선정, 만월당, 농산리 고석불등 볼만한 것이 널여있고, 월성계곡으로 이어지는 월성천변에는 펜션, 물놀이공간, 맛집을 비롯하여 교육적 체험프로그램을 할수 있는곳이 여러곳 있다.','4시간 30분','식수보급처가 없으니 매점에서 구입하거나 사전준비','강동마을회관, 정온종택, 수승대','위천면 사무소 근처/마트, 편의점','경남 거창군 북상면 농산리 산 44',35.7659943,127.798202);
