INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000800','북한산둘레길','13코스 송추마을길','교현우이령입구 ~ 원각사입구(5.3km)','서울 강북구','보통','5~10Km미만','5.3','북한산 둘레길 13코스 송추 마을길. 수도권 휴양지로 널리 알려진 청정한 송추계곡(2.5km)을 만날수 있는 송추마을길은 군시절의 추억과 시골의 정취를 동시에 느낄수 있는 추억의 마을길입니다. 물장구치고 고기잡던 유년시절의 향수를 간직하고 있는 송추계곡과 짧은 계곡임에도 비온 후에는 제법 깊은 산속 폭포같은 모습으로 다가오는 원각사계곡의 시원한 물소리는 도심에서는 느껴보지 못한 자연의 작은 선물입니다.','2시간 40분','식수 보급처가 없으므로 인근마을 매점에서 구매 또는 사전준비','오봉탐방지원센터 옆, 송추주차장내(2개소)','송추계곡(송추이주단지입구) 교량입구','서울 은평구 불광동',37.6261212,126.9362684);
--행 801
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000801','북한산둘레길','14코스 산너미길','원각사입구 ~ 안골계곡(2.3km)','서울 강북구','매우어려움','1~5Km미만','2.3','북한산 둘레길 14코스 산너미길. 사패산의 깊은 속내를 살펴볼 수 있는 산너미길은 산을 넘는다는 의미로 사패산 6부능선에 있는 붉은 바위까지 올라야하는, 둘레길 중 가장 힘든 구간이지만 전망은 으뜸입니다. 전망대인 거북바위터에서 내려다보면 의정부 시가지와 멀리 양주시청도 한눈에 들어오고 오른쪽으로는 수락산이, 정면에는 천보산이 파노라마처럼 펼쳐집니다. 또한 안골계곡을 끼고 탐방하는 구간으로 계곡을 넘는 곳곳에는 사패교, 갓바위교, 울띄교 등 나무다리가 조성되어 있어 산길의 운치를 더해줍니다.','1시간 10분','식수 보급처가 없으므로 인근마을 매점에서 구매 또는 사전준비','원각사입구(1개소)','마을길구간에서 인근매점 활용','서울 은평구 불광동',37.6261212,126.9362684);
--행 802
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000802','북한산둘레길','15코스 안골길','안골계곡 ~ 회룡탐방지원센터(4.7km)','서울 강북구','보통','1~5Km미만','4.7','북한산 둘레길 15코스 안골길. 이 구간은 의정부시가 조성한 직동공원과 연결된 길로 배드민턴장, 축구장, 인공암벽과 같은 체육시설은 물론 분수대 야생화단지, 조각공원까지 한 자리에서 즐길 수 있는 가족형 산책코스입니다. 그 뿐만 아니라 안골계곡을 포함해 사패산으로 오르는 등산로가 곳곳에 이어져 있어 본격 등산을 위한 준비코스로 좋습니다. 회룡탐방지원센터에서 가까운 회룡사는 태조 이성계와 무학대사에 얽힌 전설이 전해지는 의정부시 대표 사찰로 문화재적 가치가 높은 회룡사 오층석탑, 회룡사석조, 신중도가 있습니다.','2시간 20분','회룡샘','안골계곡 입구, 회룡탐방지원센터(2개소)','회룡탐방지원센터 입구','서울 은평구 불광동',37.6261212,126.9362684);
--행 803
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000803','북한산둘레길','16코스 보루길','회룡탐방지원센터 ~ 원도봉입구(2.9km)','서울 강북구','어려움','1~5Km미만','2.9','북한산 둘레길 16코스 보루길. 고구려시대의 석축과 보루가 있어서 보루길 이라고 합니다. 회룡탐방지원센터를 지나 다락원방향으로 가파르게 오르다보면 사패산 3보루터를 만날수 있습니다. 중랑천을 따라 남북을 잇는 고대 교통로를 통제하던 보루답게 보루터에서는 의정부시 호원동일대가 한눈에 내려다 보입니다. 이 길은 조금 가파르게 오르내리기 때문에 난이도가 높지만 걷고 나면 상쾌한 기분을 느낄수 있는 구간이기도 합니다.','1시간 30분','식수 보급처가 없으므로 인근마을 매점에서 구매 또는 사전준비','원심사 앞(1개소)','원도봉산 입구','서울 은평구 불광동',37.6261212,126.9362684);
--행 804
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000804','북한산둘레길','17코스 다락원길','원도봉입구 ~ 다락원(3.1km)','서울 강북구','보통','1~5Km미만','3.1','북한산 둘레길 17코스 다락원길. 다락원이란 명칭은 조선시대에 공무로 출장하던 사람들이 묵던 원이 있었고, 그 원집에 다락 증 누각이 있어서 붙여진 이름입니다. 한때는 상인들이 물건을 사고 팔았던 시장이 성했던 곳이지만 지금은 명칭으로만 전해집니다. 원도봉 입구에서 다락원까지 구간의 대부분이 공원 외 지역에 걸친 구간으로 서울과 경기도의 경계를 지나는 구간이기도 합니다. 마을길을 통과하여 다락원으로 접어들면 잭슨캠프 뒤편으로 무성한 큰키나무들과 아기자기한 텃밭, YMCA다락원캠프장 등을 지나며 조용한 시골길의 분위기를 느낄수 있습니다.','1시간 30분','식수 보급처가 없으므로 인근마을 매점에서 구매 또는 사전준비','대부분 마을길구간으로 주변 상가 화장실 이용','원도봉입구','서울 은평구 불광동',37.6261212,126.9362684);
--행 805
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000805','상족암길','상족암길','임포마을선착장~(1.3km)학림권역농어촌체험센터~(0.3km)송천참다래체험마을~(3.5km)소을비포성지~(0.2km)동화어촌체험마을~(4km)맥전포항등대~(1km)입암마을~(0.4km)상족암군립공원~(0.3km)공룡박물관','경남 고성군','어려움','10~15Km미만','11','상족암길은 청정해역인 자란만을 끼고 하일면과 공룡박물관, 상족암군립공원이 있는 하이면에 걸쳐 이어집니다. 아름다운 바닷길을 걷다보면 소나무숲이 우거진 숲길을 만날 수 있고 흙길, 해변길, 논길을 따라 여유롭게 감상하시면서 걸으실 수 있습니다. 또한 코스구간 마다 있는 체험마을을 통해 가족과 함께 다양한 체험활동을 하면서 잊지 못할 추억을 남길 수 있습니다. 공룡발자국을 따라 걷는 상족암군립공원 해안은 점점이 떠 있는 크고 작은섬이 수반과 같아 남해안 한려수도를 한눈에 바라볼 수 있어 낭만적입니다. 상족암군립공원 내에 있는 고성공룡박물관은 고성의 대표적 공룡인 이구아노돈의 몸체를 형상화하여 건립된 국내 최초의 공룡박물관으로서 국제수준의 관광명소로 부각하고 있습니다.','4시간 10분','맥전포항등대, 상족암군립공원, 공룡박물관','임포마을 선착장, 소을비포성지, 맥전포항등대, 상족암군립공원, 공룡박물관','상족암군립공원, 공룡박물관','경남 고성군 하일면 춘암리 865',34.9090618,128.1831628);
