INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000806','북한산둘레길','18코스 도봉옛길','다락원 ~ 무수골(3.1km)','서울 강북구','쉬움','1~5Km미만','3.1','북한산 둘레길 18코스 도봉 옛길. 도봉산 주탐방로와 만나는 도봉옛길은 조상의 정취를 간직한 볼거리가 가득한 구간입니다. 도봉산에서도 이름난 사찰인 도봉사, 광륜사, 능원사를 지나는가하면 도봉계곡 옆에 있는 우암 송시열의 도봉동문이란 바위글씨가 명산의 입구임을 알려줍니다. 세종이 재위 당시 찾았다가 물 좋고 풍광이 좋아 아무런 근심이 없는 곳이라하여 그 이름이 유래된 무수골에는 세종의 아홉째 아들인 형해군의 묘를 비롯해 단아한 모양새를 한 왕족묘가 있습니다. 산정약수터입구에서 시작하는 220m의 무장애탐방로는 휠체어통행이 가능하며, 탐방로 끝 전망데크에 서면 선인봉과 도봉 절경이 한 눔에 들어옵니다.','1시간 30분','산정약수','산정약수 밑, 도봉소공원(2개소)','광륜사','서울 은평구 불광동',37.6261212,126.9362684);
