INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000807','북한산둘레길','19코스 방학동길','무수골 ~ 정의공주묘(1.6km)','서울 강북구','보통','1~5Km미만','3.1','북한산 둘레길 19코스 방학동길. 방학동이라는 이름은 곡식을 찧는 기구인 방아가 있는 곳이라는 뜻을 가진 우리말 방아골에서 유래했습니다. 한자로 기록하는 과정에서 음이 비슷한 방학리로 고쳐지면서 지금의 방학동이 되었습니다. 전국간이 숲길로만 이어진 방학동길에서는 이마에 맺힌 땀방울을 한 모금 약수로 식히고 갈 수 있는 바가지 약수터와 도봉산둘레길의 명소이자 유일한 전망대인 쌍둥이 전망대를 만나실 수 있습니다. 방학능선을 천천히 걸을 때 나무 사이로 언뜻언뜻 모습을 드러내는 도봉산 주봉도 풍치를 자아냅니다.','1시간 30분','바가지약수터','산행중에는 공중화장실이 없으므로 무수골 입구 화장실 활용','정의공주묘 입구','서울 은평구 불광동',37.6261212,126.9362684);
--행 808
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000808','소백산 자락길','01코스','선비길 : 선비촌 - 순흥향교 - 송림호 - 배점분교(3.8km, 55분) 구곡길 : 배점분교 - 죽계구곡 - 초암사(3.3km, 50분) 달밭길 : 초암사 - 쇠자우골 - 달밭골 - 성재 - 비로사 - 삼가주차장(5.5km, 75분)','경북 영주시','어려움','10~15Km미만','12.6','소백산자락길 01코스. "문화생태탐방로" 로 가족여행객에게 가장 인기가 높은 자락길이다. 100살은 족히 넘어 보이지만 선비의 곧은 마음 만큼이나 높게 뻗은 소수서원 소나무숲길에서 시작되며, 조선500년을 관통하는 유학이념이 1자락 곳곳에 위치한 문화유산에 고스란히 녹아있다. 과거시험을 치르기 위해 한양으로 몽들던 선비들이 한 번쯤 지나쳤을 법한 이곳은 아직도 까마득한 숲길이고 보드라운 흙길로 보존되어 있다. 산수의 아름다움과 더불어 예로부터 신성시되고 명당으로 여겨져 수많은 명현을 배출한 이곳에서 옛 선비가 된 듯 "선비걸음"으로 천천히 아름다운 풍광을 만끽하며, 생생한 역사를 만나보자','4시간 30분','식수보급처가 없으니 매점에서 구입하거나 사전준비','소수서원, 선비천, 초암사,비로사','출발점인 선비촌 일원, 도착점인 삼가주차장 일원, 중간에는 없음','충북 단양군 가곡면 보발리',37.0289931,128.4480478);
