INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000811','소백산 자락길','02코스','학교길 : 삼가주차장 - 삼가분교 - 금계호 - 영전펜션마을(3.7km, 60분) 승지길 : 금계호 - 금선정 - 임실마을 - 풍기소방서(4.0km, 70분) 방천길 : 남원천(유다리) - 방천길 - 소백산역(7.9km, 130분)','경북 영주시','어려움','15~20Km미만','15.6','소백산자락길 02코스. 소백산자락길 탐방로 중 유일하게 기차역이 통과하는 코스로 열차를 이용한 탐방객이 걷기에 좋은 곳이다. 조선시대 정감록의 십승지 중 제1승지로 손꼽히는 이곳은 오감만족 여행지로 손색이 없다. 자연이 짖어내는 아름다운 풍광을 보며 세상의 온갖 시름을 잊게 만드는 2자락의 매력은 풍부한 먹을거리, 볼거리, 체험거리를 한꺼번에 즐길 수 있다는 점이다. 인삼재배지로 유명한 풍기에서 맛깔스런 음식, 인삼재배체험, 사과따기 체험 등의 다양한 농촌체험, 전국에서 으뜸가는 유황온천인 소백산풍기온천리조트 체험까지 다양한 팔색조 매력을 가진 오감만족 여행을 즐겨보기 바란다.','4시간 30분','식수보급처는 없으나 타 탐방로에 비해 매점이 많아 구입 가능하나 사전준비 최고','삼가주차장, 풍기온천, 소백산역','출발점인 삼가주차장 및 중간지점 풍기소방서 ~ 남원천 구간','충북 단양군 가곡면 보발리',37.0289931,128.4480478);
