INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000818','태안 해변길','01코스 바라길','학암포자연관찰로~학암포탐방지원센터~모래포집관판데크~먼동해변~먼동전망대~능파사~모재쉼터~신두리사구~신두리해변','충남 태안군','보통','10~15Km미만','12','바다의 고어인 "아라"에서 그 명칭이 유래된 바라길은 상큼한 산림향과 싱그로운 바다내음을 느낄 수 있는 곳이다. 학암포-구례포-먼동-신두리로 이어지는 아름다운 해변과 숲길을 거닐다 보면 한 폭의 산수화에 빠진 듯한 기분이 들 것이다. 바라길의 시점인 학암포자연관찰로에서는 셀프가이드가 가능하도록 다양한 동?식물을 소개하는 해설안내판이 설치되어 있어 아이들을 동반한 가족단위 탐방객에게 추천할 만한 코스이며, 모래와 바람의 나라 궁전을 연상케 해주는 학암포탐방지원센터에 들러 탁트인 학암포해변의 경관을 한눈으로 담아갈 수 있다. 또한 구례포해변의 유리 사구관찰 데크를 걸으며 바람에 날린 모래가 언덕을 이루는 모습을 관찰 할 수 있고, 아담하고 깨끗한 먼동해변의 매력적인 모습을 볼 수 있다. 우리나라 최대의 해안사구인 신두리 사구(천연기념물 제431호)와 람사르협약에 의해 지정된 두웅습지를 방문하면 독특한 생물들을 만나볼 수 있다.','4시간','능파사 입구','학암포 탐방지원센터, 구례포해변, 먼동해변, 능파사, 신두리','출발점인 학암포, 도착점인 신두리 해변, 중간에는 없음','충남 태안군 안면읍 창기리 1269-70',36.5841566,126.313224);
