INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000819','갈맷길','07-01코스','성지곡수원지(어린이대공원) - (5.0km / 110분) - 만덕고개 - (2.3km / 70분) - 남문 - (2.0km / 60분) - 동문','부산 부산진구','쉬움','5~10Km미만','9.3','성지곡 수원지 삼나무와 편백 숲길이 들머리다. 백양산 갈림길에서 한국산개구리 보호지역 쇠미산 습지를 지나 송전탑이 있는 능선을 따라 만덕고개로 향한다. 금강공원으로 오르는 길에서 뒤돌아보면 사행하면서 흐르는 온천천과 동래구 일원의 도시경관을 볼 수 있다. 금정산성 제2망루 가는 길까지는 다소 숨이 차나, 남문을 통과한 다음 산성고개에서 동문을 지나 부채바위, 제4망루, 원효봉, 북문에 이르는 능선길은 부산 전체를 조망하는 시원한 길이다. 금정산성은 우리나라에서 가장 규모가 큰 산성으로 문루 4개소, 망루 4개소가 소재해 있다. 산성 내 산성마을에서 생산되는 막걸리와 염소고기는 그 맛이 뛰어나 등산객들의 발길을 사로잡는다.북문 고산습지에서 천년고찰 범어사가 있는 계곡을 따라 내려와서 팔송까지 금어동천, 비석골 서어나무 숲을 호젓이 걷는 옛길이 펼쳐진다. 노포역에서 연꽃농장을 지나면 수영강 중상류를 따라 회동수원지 상현마을에 이른다.','4시간','','성지곡수원지','성지곡수원지','부산 기장군 일광면 이천리',35.2732547,129.2465619);
