INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000830','태안 해변길','06코스 샛별길','꽃지해변~리솜리조트 곰솔림~병술만~샛별해변~황포항','충남 태안군','보통','10~15Km미만','13','샛별길의 시작점인 꽃지 해변은 할미 할아비바위 사이로 서해의 3대 낙조를 감상할 수 있는 곳이다. 해변을 따라 걷다보면 병술만이라는 곳에 도착하게 되는데 이곳은 바닷물이 내륙 깊숙이 드나드는 곳으로 고려시대 몽고의 침략에 항거한 삼별초가 주둔하며 훈련했던 천혜의 군사요충지로 기록되고 있다. 그 이후 몽돌로 이루어진 샛별해변에 가면 동해안 못지 않은 맑은 물과 자갈 사이로 부딪히는 파도소리가 그간 지친 우리내 마음에 청량감을 불러일으키기 충분하다. 황포항으로 넘어가는 언덕을 올라가다보면 쌀과 특산품을 싣던 배들이 많이 좌초되었다 하여 유래된 쌀썩은 여 해변을 볼 수 있다. 이곳 해변은 아름다운 기암괴석이 솟아 있어 다른 곳에서는 볼 수 없는 독특한 조화로움을 만끽할 수 있다. 도착지인 황포항은 홍수로 인해 갯벌에 누런 황토물이 흐른다는 데서 지명이 유래된 곳인데 지금은 해안을 따라 설치된 방조제로 인해 민물의 유입이 적어 황토물의 흐름을 보기는 어려워졌다.','4시간','식수보급처가 없으니 매점에서 구입하거나 사전준비','꽃지해변, 리솜리조트 인근, 병술만, 샛별해변, 황포항','출발점인 꽃지해변, 샛별해변, 도착점인 황포항','충남 태안군 안면읍 창기리 1269-70',36.5841566,126.313224);
