INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000836','지리산둘레길','12-01코스(하동읍 ∼ 서당)','하동읍-바람재(2.5km)-관동(2.3km)-상우(1.4km)-서당(0.8km)','경남 하동군','보통','5~10Km미만','7','지리산 둘레길 12-01코스(하동읍 ∼ 서당). 차향이 당기고 매화향이 매혹하는 너뱅이들 건너 오솔길 대중교통을 이용해 하동읍에서 지리산둘레길을 드나드는 길이다. 하동에서 대중교통을 이용해 지라산둘레길에 가는 대신 걸어서 가는 길이다. 하동읍에서 차밭길을 통해 서당마을에 이르는 구간이 좋다. 하동읍의 시원한 너뱅이들과 적량들판의 모습에서 넉넉한 농촌의 삶을 오롯이 느끼며 걷게 된다. 봄이면 산속 오솔길에서 매화향이 진동한다. 비교적 짧은 구간이라 부담 없이 산책하듯 걸으면 좋다. 서당마을에서 대축~삼화실 구간과 이어진다. 어느방향으로 가도 무방하다.','3시간','하동안내센터, 서당마을회관','하동안내센터, 우계저수지','하동시장','전북 남원시 인월면 인월리',35.4592144,127.6018698);
