INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000837','자굴산 둘레길','쇠목재 정자 ~ 달분재 정자','쇠목재 정자 ~ (2.2km)절터샘 ~ (0.4km)금지샘 ~ (1.1km)달분재 정자','경남 의령군','보통','5~10Km미만','7.4','자굴산 들레길의 능선부에는 천년 다지송과 희귀목인 노각나무, 사람주나무, 비목나무 등이 자태를 뽐내고 있고 다양한 수목 군락지를 만날수 있다 또한 골골이 많은 전설을 지닌 기암괴석과 풍광을 접할수 있어 보는 이로 하여금 감탄을 자아내게 한다. 특히 이길은 종전 수직 상하 이동의 힘든 등산개념에서 누구나 쉽게 접근 이용토록 수평개념의 등산로를 조성하여 자굴산만의 생태와 진면목을 느끼고 심신을 단련토록 둘레길을 조성하였다','없음','식수보급처가 없으니 매점에서 구입하거나 사전준비','쇠목재 화장실','의령읍, 가례면 소재지','경남 의령군 대의면 신전리 산 1-2',35.3869984,128.2024235);
--행 838
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000838','감악산 물맞이길','01코스 물 맞으러 가는 길','방문자센터 →(3.07㎞) 선녀탕 →(0.8㎞) 선녀폭포 →(1.85㎞) 물맞는약수탕','경남 거창군','보통','5~10Km미만','6','감악산 물맞이길은 거창의 남상면에 위치한 감악산을 둘러보는 길이다. 코스는 총 4코스로 구성되어 있으며 각각 1코스 물맞으러 가는 길, 2코스 고행의 둘레길, 3코스 전망대 가는 길, 4코스 심신도량 하는 길로 이루어져 있다. 1코스 물맞으러 가는 길은 매산마을(방문자센터)에서 연수사로 가는 옛길로 상매산 마을과 지겟길을 올라 매산저수지까지는 포장된 농로로 되어 있고, 매산저수지 준설로 옛길은 물속에 잠겨졌으며, 잠시 과수원 옆으로 포장된 도로를 따라 오르면 평전다리가 있는 계곡에 이른다. 여기서 선녀계곡을 따라 이어진 오솔길 주변은 계곡과 천연림이 어우려져 있으며, 이 계곡물은 연수사 약수터에서 발원된 물로 맑고 청아하여 매년 칠석이면 선녀들이 내려와 목욕을 즐기고 승천하였다고 전하며, 선녀폭포에서 농로 길을 따라 오름 갈림길에서 연수사 부도 방향으로 숲속을 오르면 연수사와 물맞는 약수탕에 이르게 되며, 또 다른 코스를 선택하여 오름과 내림을 하면 된다.','2시간 30분(편도)','연수사','가제골 주차장, 연수사','남상면소재지','경남 거창군 남상면 무촌리 산 315',35.6039034,127.9194286);
