INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000839','감악산 물맞이길','02코스 고행의 둘레길','가제골주차장 →(0.2㎞) 선녀폭포 →(2.0㎞) 참나무평원 →(1.1㎞) 전망대 →(1.1㎞) 감악재(구절초언덕) →(2.1㎞) 약수터 →(1.1㎞) 가제골주차장','경남 거창군','보통','5~10Km미만','7.6','선인들께서 감악산 일대에 약초도 캐고, 지게를 메고 농사일과 땔감을 하던 길로 어려웠던 옛 시절을 회상하며, 현실의 역경을 떨쳐버리고 새로운 출발점이 되는 길이기를 바랍니다. 주변의 산약초와 들꽃과 교감하면서 넓은 군락을 이루고 있는 소나무와 참나무 숲길을 오르면 정상에 다다르고 포장된 도로로 내림하면서 감악평원, 구절초언덕, 약수터, 청연마을를 둘러보는 길이며, 오는길에 연수사 주변과 청연마을에 위치한 거창사건추모공원을 둘러보는 매력도 있다.','3시간','연수사, 약수터','가제골 주차장, 연수사','남상면소재지','경남 거창군 남상면 무촌리 산 315',35.6039034,127.9194286);
