INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000841','감악산 물맞이길','03코스 전망대 가는길','가제골주차장 →( 0.2㎞)선녀폭포 →(0.2㎞)오름길 →(1.8㎞)해맞이 명소 →(0.3㎞)전망대','경남 거창군','매우어려움','1~5Km미만','2.5','가제골 주차장, 선녀폭포에서 정상으로 가는 지름길이다. 오름길의 가파른 계단길을 올라 능선을 따라 오르면, 연수사 방향의 길목이 나타나며, 계속 올라가면 해맞이 행사장이 있고, 좌측으로 전망대가 있다. 내림길은 감악평전과 연수사 가는 길을 선택 할 수 있다. 이 코스는 경사가 심하여 체력의 한계를 느낄 수 있으니 참고하시기 바랍니다.','1시간 30분(상행)','식수 보급처가 없으므로 사전준비','가제골 주차장','남상면소재지','경남 거창군 남상면 무촌리 산 315',35.6039034,127.9194286);
