INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000843','감악산 물맞이길','04코스 심신도량 하는 길','연수사 → (심신도량하는길) → 물맞는 약수탕 → 연수사(1.5㎞)','경남 거창군','매우쉬움','1~5Km미만','1.5','연수사 경내 주변을 산책하는 코스로 경내는 약수터, 물맞는 약수탕, 은행나무(경상남도 기념물 제124호)가 있으며, 소나무 숲 오솔길에 부도의 쉼터가 있으며, 명상과 사색의 시간을 가지면서 조용한 산사에서 이름모를 새들과 산약초, 들꽃과의 교감도 권해본다.','40분','식수 보급처가 없으므로 사전준비','가제골 주차장','남상면소재지','경남 거창군 남상면 무촌리 산 315',35.6039034,127.9194286);
