INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000844','지리산둘레길','21코스(산동 ∼ 주천)','산동면사무소-현천마을(1.9km)-계척마을(1.8km)-밤재(5.2km)-지리산유스호스텔(2.7km)-주천안내소(4.3km)','전남 구례군','보통','15~20Km미만','15.9','지리산 둘레길 21코스(산동 ∼ 주천). 봄마다 세상을 노랗게 물들이는 돌담 옆 산수유 물결 전라남도 구례군 산동면과 전라북도 남원시 주천면을 잇는 15.1km의 지리산둘레길. 이 구간은 지리산의 영봉 노고단을 바라보며 걸을 수 있고, 편백나무숲에서는 상쾌한 피톤치드를 마실 수 있다. 계척마을에서는 우리나라에서 가장 오래되었다는 할머니 산수유나무를 만나고, 정겨운 돌담길도 걷게 된다. 특히 봄철이면 현천마을까지 이어진 산수유군락이 장관을 이룬다.','7시간','산동면사무소, 밤재, 주천안내소','산동면사무소, 밤재, 주천안내소','산동면, 주천농협','전북 남원시 인월면 인월리',35.4592144,127.6018698);
