INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000845','화왕산스토리테마 둘레길','01코스 비들재암릉길','비들재 ~ (1.28km)헬기장 ~ (1.25km)벽바위 ~ (1.50km)산불초소 ~ (0.26km)배바위 ~ (0.55km)화왕산성 ~ (0.37km)화왕산 ==> 하산코스 등산객 선택','경남 창녕군','어려움','5~10Km미만','5.3','기존의 화왕산~비들재구간을 재정비한 코스로 장군바위, 배바위, 벽바위 등 괴암절벽의 비경과 절경을 감상할 수 있고, 화왕산성 안에는 10리 억새밭 및 진달래 군락지의 아름다운 경치를 즐길수 있다.특히, 화왕산 진달래가 유독 붉고 아름다운 것은 화왕산성을 지키던 호국영령들의 뜨거운 단심때문일 것이다.','2시간 30분','식수보급처가 없으니 등산하기전 사전준비','허준세트장','없음(음식,음료 사전준비)','경남 창녕군 창녕읍 퇴천리',35.5176846,128.5312197);
