INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000846','동의보감 허준순례길','01코스 동의보감 허준 순례길','○ 1코스 : 한의학박물관 → 한방약초체험테마공원 → 전망대 → 사슴사육장 → 기체험장 → 구름다리 1 → 구름다리 2 → 동의본가 ○ 2코스 : 기체험장 → 해부동굴 → 곰상 및 정자 → 한의학박물관 ○ 3코스 : 해부동굴 → 약수터','경남 산청군','보통','1~5Km미만','1','의성 허준선생 등 수많은 명의가 왕산과 필봉산에서 의술활동을 펼친 배경으로 보다 쾌적하고 아름다운 휴식공간을 마련하고자 체험형 테마길로 조성한 곳이다','1시간','약수터 및 음수대 비치','한방기체험장1곳, 해부동굴 앞1곳, 박물관 내 1곳, 불로문 앞 1곳','출발점인 불로문 앞 매점있음','경남 산청군 금서면 특리 1300-25',35.4379968,127.8268801);
