INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000883','노을나루길','노을나루길','부산광역시 사하구 하단동 낙동강하구둑 입구 ~ 을숙도대교 간 1.67km','부산 사하구','매우쉬움','1~5Km미만','1.67','노을나루길은 낙동강하구둑~을숙도대교 구간의 보행로와 산책로 1.67km를 확장해 녹색길로 조성하였다. 낙동강 하구는 강과 바다가 만나는 곳으로 해가 질 무렵이면 황금빛으로 물든 하늘과 그 붉은 빛이 바다에까지 비치어 장관을 연출한다. 전국 최고의 일몰을 감상할 수 있는 있는 곳으로 강에 비친 노을을 의미하는 ''노을나루길''이라는 명칭이 생겼다. 예로부터 이곳의 낙조는 ‘다대팔경’의 하나로 손꼽히며 이와 더불어 떼지어 날아가는 철새들을 볼 수 있는 아름다운 곳이다. 도보로 30분이면 둘러볼 수 있으며 해질녘이면 이런 아름다운 풍경을 볼 수 있어 더욱 좋다.','30분','식수공급 안됨','낙동강하구둑 좌완 나루쉼터 내, 을숙도대교 밑','매점 없음','부산 사하구 신평동',35.0953611,128.9553235);
