INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000885','가람낙조길','01코스 가람낙조길','진흥사 앞(금곡4주공단지) ~ 낙동강전망대 ~ 화명수목원 ~ 율리패총 ~ 전망대 ~ 진흥사 앞(금곡주공4단지)','부산 북구','보통','10~15Km미만','10.1','천혜의 자연경관인 낙동강을 바라보며 부산의 명산인 금정산을 체험할 수 있으며 화명수목원, 대천천, 생태공원 등 친환경 생활공간과 더불어 금곡동의 율리 패총, 알터, 열녀 이씨 정려비 등 우리 지역의 문화 유적지와도 연계되어 있어 주민들이 지역문화와 녹색 웰빙길을 같이 체험하며 생활에 건강과 활력을 찾게 되는 산책길임','3시간 40분','화명수목원','화명수목원','화명수목원','부산 북구 화명동',35.2517548,129.0372702);
