INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000896','울산어울길','04코스','기령재(마우나오션리조트 입부, 북구 매곡동 산9-7)~삼태봉 갈림길~이화초등학교~동천~관문성~천마산~만석골저수지','울산 북구','보통','10~15Km미만','11.5','기령에서 이삭귀개 등 희귀식물이 서식하는 돌티미늪을 지나 이화마을, 경주와 울산사람들이 함께 쌓았다는 관문산성, 천마산, 만석골저수지로 이어지는 길이다. 특히, 천마산은 소나무와 편백나무가 잘 조성되어 삼림욕을 즐길 수 있다.','6시간','별도준비','만석골 저수지','','울산 북구 가대동',35.6093334,129.2993885);
