INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000900','강동사랑길','01코스','정자항 → 활만송 → 유포석보 → 박제상발선처 → 정자항과 귀신고래등대','울산 북구','보통','1~5Km미만','3.2','600년 민중의 역사와 함께 해 온 활만송, 왜구를 막던 유포석보, 왜국으로 죽음의 길을 갔던 충신 박제상의 혼이 머무른 발선처 등 충심과 신의가 횃불처럼 이어져 내려오는 역사가 있는 이야기길이다.','1시간 30분','주변 매점이나 식당에서 충분히 구입 가능','정자항 공중화장실','정자회센터 주변에 여러 곳','울산 북구 정자동',35.6151717,129.4438118);
