INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000902','강동사랑길','03코스','제전항~일심전망대(1.8km)~옥녀봉(0.6km)~제전항','울산 북구','보통','1~5Km미만','4.7','옹녀와 강쇠의 사랑이야기가 전해지는 싱그러운 산길은 숲속의 호텔이라는 천이궁과 옹녀가 세상의 선녀가 되엇다는 옥녀봉으로 이어지는 길이다. 제법 경사가 있어 운동삼아 등산을 즐기기에도 좋은 코스이며, 연인과 다정하게 제전마을의 벽화길을 걸어보는 것도 즐거운 일이다.','2시간 10분','','사랑길 제전장어 밖','제전슈퍼','울산 북구 정자동',35.6151717,129.4438118);
