INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000903','강동사랑길','04코스','제전항~금실정(1.3km)~해녀의 집(0.8km)~까치전망대(2.1km)~제전항','울산 북구','보통','5~10Km미만','5.9','부부가 된다는 것이 어찌 행복하고 평온한 길만 있겠는가. 때때로 폭풍우치는 성난 바다같은 고통과 발목을 시큰거리게 하는 자갈길, 숨이 턱끝까지 차오르는 힘겨운 길도 있다. 그럼에도 불구하고 이일송처럼 서로 기대며, 보듬고 위로하고, 마주보면서 하나가 되어가는 인내와 이해의 길이다. 4구간에서는 강동사랑길의 최고 명소인 까치전망대에서 정자항과 당사항을 한눈에 담을 수 있으며 출발지인 제전항으로 돌아와 제전장어에서 바다장어의 깊은 맛을 느껴볼 수 있다.','2시간 30분','','사랑길 제전장어 밖, 금실정','해파랑가게','울산 북구 정자동',35.6151717,129.4438118);
