INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000904','강동사랑길','05코스','당사항~강동축구장(0.7km)~산해로(0.6km)~당사항','울산 북구','보통','1~5Km미만','2.9','500년을 산 느티나무는 우리에게 무슨 이야기를 들려 주려는 걸까? 긴세월 마을의 안녕을 빌던 당당한 정기와 파란 바다를 배경으로 펼쳐진 초록의 잔디구장과 자연학습장이 있다. 울산지역의 마지막 봉수대였던 우가산 봉수대는 오랜 세월의 흔적을 간직한 채 첨단정보화 시대를 말없이 지키고 바다를 잇댄 산길의 고즈넉한 평온이 파도소리에 깨어난다. 안전하게 걷기여행을 할 수 있도록 찻길 위 능선을 따라 산해로가 이어진다. 산과 바다가 나란히 이어지는 산해로는 강동사랑길 중 걷는 기분이 최고로 좋아지게 만드는 길이다.','1시간 30분','강동축구장','강동축구장','','울산 북구 정자동',35.6151717,129.4438118);
