INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000908','바우길','대관령 국민의숲길','대관령하행휴게소→ 야생화숲길→ 능경봉입구(샘터)→ 국민의 숲 산림트레킹코스→ 남경식당→ 래포빌펜션→ 잎깔나무숲길입구→ 바우길 1구간분기점→ 바우길 2구간분기점→ 양떼목장 → 대관령하행휴게소','강원 강릉시','보통','10~15Km미만','11','강릉 바우길 대관령 국민의 숲길 . 이 길은 우리나라 산림조성의 역사와 잘 가꾼 숲길을 전형을 보여주는 구간입니다. 고원지대에서 보기 힘든 넓은 암반 사이로 물이 흐르는 계곡을 따라 걷다가 끝없이 이어지는 낙엽송과 전나무 숲 사이로 난 오솔길을 걸으면 숲길 걷기의 행복도 길따라 끝없이 이어집니다. 겨울에는 눈길로 아름답고 봄부터 가을까지 철마다 달리 피어나는 야생화가 이 길을 찾는 길손을 반깁니다.','4시간','출발지와 대관령 레포빌','구영동고속도로 대관령휴게소','출발지, 도착지','강원 평창군 대관령면 횡계리',37.7040465,128.7490955);
--행 909
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000909','바우길','대관령 눈꽃마을길','눈꽃마을 산촌생태체험장→ 고원목장입구→ 승마클럽→ 켄터기펜션단지→ 대관령 소심원펜션→ 능선 소나무쉼터→ 능선 우측숲길 → 잠수교','강원 강릉시','보통','10~15Km미만','12','강릉 바우길 대관령 눈꽃마을길 . 동양의 알프스라 불리는 대관령 고원목장에서 한가로이 풀을 뜯는 소와 말과 양과 산양들의 생태를 감상합니다. 여러 개의 목장 사잇길을 걷다보면 이곳이 들인지 산인지조차 헷갈리게 합니다. 눈꽃마을 오솔길은 우리나라에서 가장 높은 곳에 위치한 트레킹 코스로 깨끗한 공기와 탁 트인 전망이 길을 걷는 사람들의 시름을 저절로 내려놓게 합니다. 참나무숲 아래로 끝없이 펼쳐진 산죽밭길도 잊을 수가 없습니다.','5시간','대관령 눈꽃마을 생태체험장','대관령 눈꽃마을 생태체험장','횡계에서 미리 매점을 이용하여야 함.','강원 평창군 대관령면 횡계리',37.7040465,128.7490955);
