INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000910','해파랑길','01코스','오륙도해맞이공원~(4.8km)동생말~(4.0km)광안리해변~(6.7km)APEC하우스~(2.2km)미포','부산 남구','보통','15~20Km미만','17.8','해파랑길 전체 770㎞ 중에서 제1선발인 부산은 의외성을 갖는 멋진 길의 변화가 걷는 이들을 시시때때로 감동시킨다. 시작점에 있는 해파랑길 종합안내소에서 이어지는 ‘이기대길’ 구간부터 경탄과 감탄을 자아내는 해식절벽의 비경으로 아름답다. 이기대 해안의 절벽길은 기존 해안순찰로를 정비하여 위험한 곳은 나무데크와 울타리로 안전하게 바꾸어 산책로 수준으로 조성한 명품길이다. 광안리해변은 광안대교의 웅장한 위용과 고운 백사장이 장도를 시작한 나그네의 마음을 쿵쿵 뛰게 한다. 해운대는 신라 최치원이 속세를 버리고 가야산으로 들던 길에 빼어난 경치에 반해 자신의 자(字)인 해운(海雲)을 바위에 새겨 넣은 후 해운대라 불리게 되었다. 지금도 동백섬 바위에 최치원이 새겼다는 해운대 글씨가 또렷하다.','6시간','약수터는 없음. 매점에서 사전 준비 필요','오륙도 해파랑길안내소, 어울마당, 광안리해수욕장, 광안리수변공원, 동백섬, 해운대 등','이기대어울마당, 동생말, 광안해수욕장, 해운대해수욕장 등','경북 영덕군 영덕읍 창포리 산 5-5',36.4273739,129.4352783);
