INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000913','바우길','울트라 바우길','안인진주차장→ 덕우리재→ 삽당령→ 닭목령→ 대관령→ 바우길 게스트하우스','강원 강릉시','매우어려움','20~100Km미만','74.4','강릉 바우길 울트라바우길. 동해안 안인에서 부터 출발해 백두대간 능선을 따라 닭목령과 선자령을거쳐 보광리 바우길 게스트하우스로 돌아오는 울트라 바우길은 고난도트레킹과 야영이 혼합된 천혜의 자연속을 3박4일간 72km를 걷는 백두대간의 축소판과도 같은 구간입니다. 새로운 걷기에 도전하실 분들은 작은 백두대간 울트라 바우길의 천연 정기를 받아 보세요.','4박 5일','','','','강원 평창군 대관령면 횡계리',37.7040465,128.7490955);
