INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000915','제주 올레길','01-01코스 우도 올레','천진항 → 쇠물통언덕 0.9km → 홍조단괴해빈 해수욕장 2km → 하우목동항 3.2km → 망루 앞 삼거리 5.6km → 파평윤씨 공원 6.4km → 하고수동 해수욕장 7.3km → 검멀레 해수욕장 12km → 우도봉 13.4km → 천진항 15.4km','제주 서귀포시','보통','15~20Km미만','15.4','제주올레길 01-01코스 우도올레. 제주도에 딸린 62개 섬 가운데 가장 크고 일년 내내 쪽빛 바다색을 자랑하는 우도의 절경을 만끽할 수 있는 코스. 푸른 초원과 검은 돌담, 그리고 등대가 가장 제주다운 풍경을 연출하는 ''섬 속의 섬 우도''를 한 바퀴 도는 코스. 전체적으로 큰 굴곡이 없지만, 차와 스쿠터 등에 주의하며 걸어야 한다.','4~5시간','없음','하우목동항 부근, 하고수동해수욕장부근, 우도봉 입구 부근, 천진항 부근','하우목동항 부근(일반매점), 천진항 부근(일반매점), 우도봉 입구 부근(CU)','제주특별자치도 서귀포시 서귀동 316-1',33.2374652,126.5153178);
