INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000916','지리산둘레길','12코스(삼화실 ∼ 대축)','삼화실(삼화실안내소)-이정마을(0.4km)-버디재(1.3km)-서당마을(1.6km)-신촌마을(3.3km)-신촌재(2.7km)-먹점마을(1.9km)-먹점재(1km)-미점마을(1.8km)-대축마을(2.7km)','경남 하동군','보통','15~20Km미만','16.7','지리산 둘레길 12코스(삼화실 ∼ 대축). 눈부신 지리산, 하늘과 강을 품다 경상남도 하동군 적량면 동리(삼화실)에서 하동군 축지리 대축마을을 잇는 16.9km의 지리산둘레길. 이 구간은 마을도 많이 지나고 논, 밭, 임도, 숲길 등 다양한 길들이 계절별로 다른 모습을 선사한다. 봄에는 꽃동산을 가을이면 황금으로 물든 풍요로운 지리산자락을 펼쳐 놓는다. 먹점재에서 미동마을로 가는 길에 만나는 섬진강과 형제봉 능선, 섬진강 건너 백운산 자락이 걷는 길을 풍부하게 한다. 천연기념물로 지정된 악양면 대축의 문암송은 생명의 존엄성을 다시 한번 되새기게 해준다.','7시간','삼화실, 서당마을회관','삼화실, 우계저수지, 미점마을','삼화실, 대축마을','전북 남원시 인월면 인월리',35.4592144,127.6018698);
