INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000921','해파랑길','06코스','덕하역~(3.9km)~선암호수공원(6.3km)~울산대공원~(3.6km)~고래전망대(1.8km)~태화강전망대','울산 남구','보통','1Km미만','15.6','덕하역(동해남부선)에서 걷기 시작하면 20분 만에 솔마루길과 이어지는 숲길이 시작된다. 두왕사거리를 출발점으로 하는 이 숲길은 솔마루길이 본격적으로 시작되는 선암호수공원까지 2.5㎞ 길이로 이어진다. 선암호수공원을 빙 둘러가면 신선산으로 올라가는 솔마루길 입구가 나온다. 나무계단을 따라 조금만 올라가면 솔마루길이란 이름에 걸맞는 송림길이 걷기여행자들을 부드럽게 안내한다. 신선산 정상의 신선정에서 바라보면 선암호수공원과 주변의 경관도 일품이다. 신선산을 지나면 잠시 마을길을 지나 솔마루다리를 건너 다시 울산대공원 숲길로 들어선다. 수없이 많은 시민이 지났을 반질반질한 숲길을 한참 걸으면 ‘솔마루 하늘길’이라는 대형 생태육교가 나온다. 상당한 높이의 이 육교에서 밑을 내려다보는 것도 이 길의 잔재미다. 남산근린공원으로 이어지는 솔마루길의 끝은 태화강을 한눈에 내려다보는 태화강전망대와 가깝다.','6시간 30분','숲길 전반부에 한 곳의 식수대가 있음','덕하역, 솔마루길 중간지점, 태화강전망대','덕하역, 선암호수공원, 태화강전망대','경북 영덕군 영덕읍 창포리 산 5-5',36.4273739,129.4352783);
