INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000929','해파랑길','10코스','정자항~(2.8km)~강동화암주상절리~(3.9km)~관성해변~(6.3km)~음천항벽화마을~(1.1km)~나아해변','울산 북구','매우쉬움','10~15Km미만','14.1','울산 정자항을 출발하여 강동해변, 신명해변, 시도간 경계를 지나 경주 관성해수욕장, 수렴리해변, 나아해변까지 이르는 10코스는 지속적으로 이어지는 몽돌해변과 강동화암, 읍천해안 주상절리가 절묘하게 어우러진 해안장관의 절정을 이루는 코스이다. 특히 양남면 하서항부터 읍천항 벽화마을까지 1.7km가량 조성된 은 일반 주상절리와는 확연히 다른 누워있는 주상절리, 부채꼴 주상절리 등 독특한 자연자원을 만날 수 있어 그 가치가 뚜렷하며 자연이 수놓은 장관을 감상할 수 있는 최고의 여정으로 손색이 없다. 또한 파도소리길을 벗어나자마자 등장하는 그림 있는 마을 읍천항 갤러리의 이색적인 풍경은 눈길을 붙잡으며, 정자 대게, 참가자미 등의 지역해산물을 풍족히 만날 수 있는 다양한 먹거리 볼거리가 산재한다.','5시간','정자해안공원, 관성솔밭해변, 하서해안공원, 읍천항','정자항, 정자해변, 강동화암주상절리, 관성솔밭해변, 하서해안공원, 파도소리길, 읍천항','정자항, 정자해변, 관성솔밭해변, 해파랑가게(관성해변-해변수퍼, 나아해변-해변수퍼), 읍천항','경북 영덕군 영덕읍 창포리 산 5-5',36.4273739,129.4352783);
