INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000931','해파랑길','11코스','나아해변~(6.3km)~봉길해변(문무대왕릉)~(2.4km)~감은사지~(1.2km)~이견대~(6.9km)~전촌항~(2.0km)~감포항','경북 경주시','보통','15~20Km미만','18.8','문무왕의 호국정신이 살아 숨쉬는 11코스는 역사적 가치가 빛나는 문화재가 집중된 역사문화탐방길이자 경주 최대의 어항과 미항이 여행객을 맞이하는 생동감 넘치는 길이다. 문무왕해중릉으로 유명한 봉길해수욕장에 도착하고 이를 시작으로 감은사지, 이견대 등의 문무왕 관련 문화재를 만나고 아름답게 정비되어 경주의 미항으로 손꼽히는 전촌항과 참가자미로 유명한 경주 최대 어항인 감포항까지 다양한 볼거리와 체험거리가 가득하다. 봉길해수욕장을 비롯한 코스 전역에서 해산물을 건조시키는 광경을 만날 수 있다. 특히 감은사지 뒤편 산으로 올라 옛이견대터를 지나 이견대로 향하는 산속 숲길은 대단히 매혹적이며, 전촌항을 지나 감포항으로 향하는 산길에 올라 정상에 오르면 갑자기 눈에 들어오는 감포항과 확 트인 바다가 연출하는 장관은 잊을 수 없는 추억을 선사한다. 역사와 문화, 산과 바다가 절묘하게 어우러진 11코스는 해파랑길의 백미라 일컬어도 손색이 없는 코스이다.','5시간','봉길대왕암해변, 나정고운모래해변, 전촌솔밭해변','봉길대왕암해변, 감은사지, 나정고운모래해변, 전촌솔밭해변, 전촌항, 감포항','해파랑가게(나아해변-해변수퍼, 봉길대왕암해변-봉길수퍼, 나정리-영광수퍼, 감포항-서울수퍼 ), 전촌솔밭해변 읍내, 감포항.','경북 영덕군 영덕읍 창포리 산 5-5',36.4273739,129.4352783);
