INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000936','해파랑길','14코스','구룡포항~(1.7km)~구룡포해변~(12.4km)~호미곶','경북 포항시 남구','쉬움','15~20Km미만','14.1','대게와 과메기의 본고장 구룡포항에서 시작하는 코스다. 구룡포 일본인가옥거리에는 아직 옛 풍경이 그대로 남아 사람들의 눈길을 끈다. 시종일관 수려한 바다을 바라보며 걷다가 연간 100만명이 찾는 일출 명소 호미곶에 이른다.','4시간 30분','호미곶 새천년기념관 이용','구룡포항내, 호미곶 화장실 이용','출발점인 구룡포항내매점, 구룡포해변매점, 도착점인 호미곶 매점','경북 영덕군 영덕읍 창포리 산 5-5',36.4273739,129.4352783);
