INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000954','해파랑길','24코스','후포항~(0.5km)~등기산공원~(2.9km)~울진대게유래비~(6.0km)~월송정~(2.3km)~대풍헌~(6.4km)~기성버스터미널','경북 울진군','쉬움','15~20Km미만','18.1','등기산의 후포등대를 돌아나와 해안도로를 걷다보면 옛날 대게가 많이 잡혔다고 하여 ‘기알’이라고 부르던 거일리를 지난다. 계속해서 해안도로를 따라 내려와 관동팔경 중의 1경으로 달빛과 어울리는 솔숲이라는 아름다운 이름을 가진 월송정을 지나 해송숲이 어울어진 구산해수욕장을 만난다. 옛날이야기와 역사가 어우러진 그림같은 코스이며 가까운 백암온천을 찾아가면 온천욕을 즐길 수 있어 걷느라 지친 심신을 잠시나마 쉬게 해준다.','6시간','식수 보급처가 없으니 매점에서 구입하거나 사전 준비 필요','후포5리 공용화장실, 해양파출소, 한마음광장 앞, 후포항, 후포6리 동회관 앞, 울진대게유래비, 거일2리 마을회관, 거일1리 마을회관, 직산2리 마을회관, 월송정, 구산해수욕장. 기성면사무소, 기성버스터미널','출발점인 후포항입구에서 후포6리 동회관 앞까지는 매점이 많으며 거일2리 동네슈퍼, 직산2리 동네슈퍼, 월송정매점, 기성버스터미널 부근 매점등이 있음','경북 영덕군 영덕읍 창포리 산 5-5',36.4273739,129.4352783);
